function initRecommended(){$(".recList").before('<span class="recLeft recBtn">&lsaquo;</span>');$(".cartRecommend").append('<span class="recRight recBtn">&rsaquo;</span>');$(".recLeft").click(function(){recUpdate("left",this)});$(".recRight").click(function(){recUpdate("right",this)})}function recUpdate(e,t){if(e=="left"){var n=$(t).parent().get(0);var r=$(n).find(".recom").slice(-4);var i=$(n).find(".recom").slice(0);$(r).reverse().each(function(e){$(n).find(".recList").prepend($(this))})}if(e=="right"){var n=$(t).parent().get(0);var r=$(n).find(".recom").slice(0,4);var i=$(n).find(".recom").slice(-1);$(r).each(function(e){$(n).find(".recList").append($(this))})}}$.fn.reverse=[].reverse;initRecommended()